// Try doing init, otherwise hook into window.resize and document.scroll and try again then. function tryInitOrHookIntoEvents(options, $that) { var success = tryInit(options, $that);
if (!success) { console.log('TST: Body width smaller than options.minWidth. Init is delayed.');
$(document).scroll(function (options, $that) { return function (evt) { var success = tryInit(options, $that);
if (success) { $(this).unbind(evt); } }; }(options, $that)); $(window).resize(function (options, $that) { return function (evt) { var success = tryInit(options, $that);
if (success) { $(this).unbind(evt); } }; }(options, $that)) } }
// Try doing init if proper conditions are met. function tryInit(options, $that) { if (options.initialized === true) { return true; }
if ($('body').width() < options.minWidth) { return false; }
init(options, $that);
return true; }
// Init the sticky sidebar(s). function init(options, $that) { options.initialized = true;
// Add CSS $('head').append($('<style>.theiaStickySidebar:after {content: ""; display: table; clear: both;}</style>'));
$that.each(function () {
var o = {};
