if (o.stickySidebar.css('position') != 'absolute') { css.position = 'absolute'; css.top = scrollTop + top - o.sidebar.offset().top - o.stickySidebarPaddingTop - o.stickySidebarPaddingBottom; }
css.width = o.sidebar.width(); css.left = '';
o.stickySidebar.css(css); } else if (position == 'static') { resetSidebar(); }
if (position != 'static') { if (o.options.updateSidebarHeight == true) { o.sidebar.css({ 'min-height': o.stickySidebar.outerHeight() + o.stickySidebar.offset().top - o.sidebar.offset().top + o.paddingBottom }); } }
o.previousScrollTop = scrollTop; };
// Initialize the sidebar's position. o.onScroll(o);
// Recalculate the sidebar's position on every scroll and resize. $(document).scroll(function (o) { return function () { o.onScroll(o); }; }(o)); $(window).resize(function (o) { return function () { o.stickySidebar.css({'position': 'static'}); o.onScroll(o); }; }(o));
// Reset the sidebar to its default state function resetSidebar() { o.fixedScrollTop = 0; o.sidebar.css({ 'min-height': '1px' }); o.stickySidebar.css({ 'position': 'static', 'width': '' }); }
// Get the height of a div as if its floated children were cleared. Note that this function fails if the floats are more than one level deep. function getClearedHeight(e) { var height = e.height();
e.children().each(function () { height = Math.max(height, $(this).height()); });
return height; } }); } } })(jQuery);
